var fs = require('fs');

function File() {
  function open(path, document, callback) {
  	fs.readFile(path, 'utf-8', function (error, contents) {
      callback(contents);
  	});
  }

  function save(path, text) {
	  fs.writeFile(path, text);
  }

  function append(path, text) {
    fs.appendFile(path, text, function(err){

    });
  }

  this.open = open;
  this.save = save;
}

module.exports = new File;
