
function placer($){

  function getBottomOfElement(element, includeBorder){
    var bottom = element.position().top;
    bottom = bottom + element.innerHeight();
    var margin = (element.outerHeight(true) - element.innerHeight())/2;
    bottom = bottom + margin;
    if(includeBorder){
      bottom = bottom +  parseInt(element.css("border-width"), 10);
    }
    return bottom;
  }

  this.getBottomOfElement = getBottomOfElement;
}

module.exports = function($){
  return new placer($);
}