
function Wallpaper(){
  function setWallpaper(wallpaperName){
    var exec = require('child_process').exec,
        path = require('path'),
        child;

    child = exec('python ' + process.cwd() + '/python/wallpaper.py ' + wallpaperName, function(error, stdout, stderr){
      console.log('stdout: ' + stdout);
      console.log('stderr: ' + stderr);
      if (error !== null) {
        console.log('exec error: ' + error);
      }
    });
  }

  this.setWallpaper = setWallpaper;
}

module.exports = new Wallpaper;
